import React, { useState, useEffect } from "react";
import { VictoryPie } from "victory";
import { Badge, Avatar, Layout } from "antd";

import { getUser } from "../../../../services/userServices";
import { getDishes } from "../../../../services/dishServices";
import { getTable } from "../../../../services/tableServices";
import { Link } from "react-router-dom";

const Charts = () => {
  const [user, setUser] = useState([]);
  const [dish, setDish] = useState([]);
  const [table, setTable] = useState([]);

  const fetchUser = async () => {
    await getUser()
      .then(res => res.data)
      .then(data => setUser(data));
  };

  const fetchDish = async () => {
    await getDishes()
      .then(res => res.data)
      .then(data => setDish(data));
  };

  const fetchTable = async () => {
    await getTable()
      .then(res => res.data)
      .then(data => setTable(data));
  };
  useEffect(() => {
    fetchUser();
    fetchDish();
    fetchTable();
  }, []);

  return (
    <Layout.Content style={{ margin: "2em 16px" }}>
      <div style={{ background: "#fff", padding: 24, minHeight: 280 }}>
        <div className="container d-flex">
          <VictoryPie
            colorScale={["tomato", "orange", "#f6b965"]}
            animate={{
              duration: 2000
            }}
            data={[
              { x: "User", y: user.length },
              { x: "Dish", y: dish.length },
              { x: "Table", y: table.length }
            ]}
          />
          <div className="d-flex flex-column">
            <Badge count={user.length}>
              <Avatar
                shape="square"
                style={
                  user.length === 0
                    ? { color: "#cc444b" }
                    : { background: "#ee6d52" }
                }
                size="large"
              >
                User
              </Avatar>
              <a href="#" className="head-example" />
            </Badge>
            <Badge className="mt-5" count={dish.length}>
              <Avatar
                shape="square"
                style={
                  dish.length === 0
                    ? { color: "#cc444b" }
                    : { background: "#f4a83c" }
                }
                size="large"
              >
                Dish
              </Avatar>
            </Badge>
            <Badge className="mt-5" count={table.length}>
              <Avatar
                shape="square"
                style={
                  dish.length === 0
                    ? { color: "#cc444b" }
                    : { background: "#f6b965" }
                }
                size="large"
              >
                Table
              </Avatar>
            </Badge>
          </div>
        </div>
      </div>
    </Layout.Content>
  );
};

export default Charts;
