import React, { Fragment } from "react";
import { Card, Row, Col, Divider, Typography, Input } from "antd";
import { PrinterOutlined } from "@ant-design/icons";

import _ from "lodash";
import PrintBill from "./printBill";
import { Link } from "react-router-dom";

const InformationList = ({ data, history }) => {
  const printBill = (bill, history) => {
    <Link to="/print" />;
    return PrintBill(bill);
  };

  return (
    <Fragment>
      <Row>
        {data.map((info) => (
          <Col key={info.id} xs={24} sm={12} md={12} lg={8} xl={6}>
            <Card
              key={info.id}
              className="m-2 border border-warning rounded"
              title={
                <p className="text-center bg-light border border-info">{`Table:${info.table}`}</p>
              }
              cover={
                <Typography.Title className="text-secondary text-center mt-4 font-weight-light text-uppercase">
                  {info.customer}
                </Typography.Title>
              }
              actions={[<PrinterOutlined onClick={() => printBill(info)} />]}
            >
              <Divider />

              <Card.Meta
                title={
                  <Input.TextArea
                    className="border border-warning rounded font-weight-bold text-uppercase text-dark"
                    value={`Orders: ${info.ordered_items}`}
                  />
                }
                // description={
                // <Typography.Title
                //   level={3}
                //   className="border border-danger bg-light rounded text-info text-center"
                // >{`Total is : ${(info = _.sum(
                //   info.price
                // ))}¥`}</Typography.Title>
                // }
              />
            </Card>
          </Col>
        ))}
      </Row>
    </Fragment>
  );
};

export default InformationList;
