import React, { useState, Fragment } from "react";
import { Card, Row, Col, Divider, Typography, Button, Input } from "antd";
import _ from "lodash";

const InformationList = ({ data }) => {
  const [totalPrice, setPrice] = useState();
  const showPrice = value => {
    const total = _.sum(value, ["price"]);
    setPrice(total);
  };
  return (
    <Fragment>
      <Row>
        {data.map(info => (
          <Col key={info.id} xs={24} sm={12} md={12} lg={8} xl={6}>
            <Card
              key={info.id}
              className="m-2 border border-warning rounded"
              title={
                <p className="text-center bg-light border border-info">{`Table:${info.table}`}</p>
              }
              cover={
                <Typography.Title className="text-secondary text-center mt-4 font-weight-light text-uppercase">
                  {info.customer}
                </Typography.Title>
              }
            >
              <Divider />
              <Typography.Text></Typography.Text>
              <Divider />

              <Card.Meta
                title={
                  <Input.TextArea value={`Orders: ${info.ordered_items}`} />
                }
                description={showPrice(info.price)}
              />
            </Card>
          </Col>
        ))}
      </Row>
    </Fragment>
  );
};

export default InformationList;
