import React, { useState, Fragment } from "react";
import { PrinterOutlined } from "@ant-design/icons";
import Pdf from "react-to-pdf";
import { Link } from "react-router-dom";
import _ from "lodash";
import { Layout, Typography, Row, Col, Divider } from "antd";

const ref = React.createRef();

const PrintBill = ({ location }) => {
  const [data] = useState(location.state);
  return (
    <Fragment>
      <Layout.Content style={{ margin: "2em 16px" }}>
        <div
          style={{ background: "#fff", padding: 24, minHeight: 280 }}
          className="border border-dark rounded"
        >
          <div
            style={{ width: 500, height: 500 }}
            className="container border border-warning rounded mt-4"
            ref={ref}
          >
            <div className="bg-light border border-dark rounded p-2 mt-3">
              <Row>
                <Col span={10}>
                  <div className="mt-2">
                    <Typography.Title>{`Table: ${data.table}`}</Typography.Title>
                  </div>
                </Col>
                <Col span={2}>
                  <Divider
                    type="vertical"
                    className="bg-dark"
                    style={{ height: "100px" }}
                  />
                </Col>
                <Col span={10}>
                  <div className="mt-2">
                    <Typography.Title
                      level={3}
                    >{`Customer: ${data.customer}`}</Typography.Title>
                  </div>
                </Col>
              </Row>
            </div>
            <Divider type="horizontal" />
            <ul>
              <Row>
                <Col span={8}>
                  {data.orders.map((order) => (
                    <li style={{ listStyleType: "none" }}>{order}</li>
                  ))}
                </Col>

                <Col span={5}>
                  {data.price.map((price) => (
                    <li style={{ listStyleType: "none" }}>
                      ................................{price}¥
                    </li>
                  ))}
                </Col>
              </Row>
            </ul>
            <Divider />
            <div className="text-center">
              <Typography.Title
                level={3}
                className="border border-info rounded bg-light "
              >{`Total Price: ${_.sum(data.price)}¥`}</Typography.Title>
            </div>
          </div>
          <div className="text-center">
            <div>
              <Pdf targetRef={ref} filename={`Table-${data.table}.pdf`}>
                {({ toPdf }) => (
                  <PrinterOutlined onClick={toPdf}>Print</PrinterOutlined>
                )}
              </Pdf>
            </div>
            <Link to="/info">Back</Link>
          </div>
        </div>
      </Layout.Content>
    </Fragment>
  );
};
export default PrintBill;
