import React from "react";
import { Form, Input } from "antd";
const CustomizedForm = ({ form, data, name }) => (
  <Form
    form={form}
    layout="vertical"
    name="form_in_modal"
    initialValues={{
      name: data.name
    }}
  >
    <Form.Item
      name={name}
      label="Name"
      rules={[
        {
          required: true,
          message: "Please input the name!"
        }
      ]}
    >
      <Input />
    </Form.Item>
  </Form>
);
export default CustomizedForm;
