// React
import React, { Fragment } from "react";

// Third part
import { Layout, Menu } from "antd";
import { LogoutOutlined } from "@ant-design/icons";
import { HashRouter as Router, Route, Link, Switch } from "react-router-dom";

// Custom

import { Footer } from "../custom/";
const { Header, Content } = Layout;

const EmployeeBoard = ({ match, history }) => {
  return (
    <Fragment>
      <Router>
        <Layout style={{ minHeight: "100vh" }}>
          <Header
            className="header"
            style={{
              position: "fixed",
              zIndex: 1,
              width: "100%",
              background: "#ffffff"
            }}
          >
            <Menu
              mode="horizontal"
              defaultSelectedKeys={["1"]}
              style={{ height: "100%", textAlign: "center" }}
            >
              <Menu.Item disabled>
                <div className="text-uppercase font-weight-bold">
                  {match.params.name}
                </div>
              </Menu.Item>
              <Menu.Item key="1">
                <span className=" menu-title ml-2">Dish Information</span>
                <Link to="/dish" />
              </Menu.Item>

              <Menu.Item key="2">
                <span className="menu-title ml-2">Personal</span>
                <Link to="/personal" />
              </Menu.Item>
              <Menu.Item key="3">
                <span className=" menu-title ml-2">Table</span>
                <Link to="/table" />
              </Menu.Item>
              <Menu.Item key="4" onClick={() => history.replace("/")}>
                <LogoutOutlined style={{ fontSize: "20px" }} />
                <span className="menu-title ml-2">Logout</span>
              </Menu.Item>
            </Menu>
          </Header>
        </Layout>
      </Router>
    </Fragment>
  );
};

export default EmployeeBoard;
